<?phpclass MAX_PLUGIN_LICENSOR_Activator {	public static function activate() {        global $wpdb;        //create max plugin file details table        $wpdb->query(            $wpdb->prepare(                "CREATE TABLE IF NOT EXISTS {$wpdb->prefix}max_plugin_details (                    id INT(255) UNSIGNED AUTO_INCREMENT PRIMARY KEY,                    plugin_name VARCHAR(255) NOT NULL,                    slug VARCHAR(255) NOT NULL,                    version VARCHAR(255) NOT NULL,                    plugin_zip VARCHAR(255) NOT NULL,                    status VARCHAR(255) NOT NULL,                    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,                    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP                  )" . $wpdb->get_charset_collate()            )        );        //create max plugin license details table        $wpdb->query(            $wpdb->prepare(                "CREATE TABLE IF NOT EXISTS {$wpdb->prefix}max_license_details (                    id INT(255) UNSIGNED AUTO_INCREMENT PRIMARY KEY,                    plugin_id INT(255) NOT NULL,                    site_name VARCHAR(255) NOT NULL,                    license_key VARCHAR(255) NOT NULL,                    license_type VARCHAR(255) NOT NULL,                    expiry_date VARCHAR(255) NOT NULL,                    status VARCHAR(255) NOT NULL,                    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,                    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP                  )" . $wpdb->get_charset_collate()            )        );	}}